import { Formik } from "formik";
import { Pressable, Text, View, StyleSheet } from "react-native";
import FormikTextInput from "./FormikTextInput";
import { Button } from "@react-native-material/core";
import React, { useState } from "react";

const initialValues = {
  username: '',
  password: '',
};

style = StyleSheet.create({
  padding: 10,
})

const SignInForm = ({ onSubmit, showPassword }) => {
  return(
    <View style={style}>
      <FormikTextInput name="username" placeholder="Username" />
      <FormikTextInput name="password" placeholder="Password" showPassword={showPassword} />
      <Button title="Sign in" onPress={onSubmit} />
    </View>
  )
}

const SignIn = () => {
  const [showPassword, setShowPassword] = useState(true);

  const onSubmit = values => {
    const username = String(values.username);
    const password = String(values.password);
    setShowPassword(showPassword === true ? false : true);

    if (username && password) {
      console.log(values);
    }
  };

  return (
    <Formik initialValues={initialValues} onSubmit={onSubmit}>
      {({ handleSubmit }) => <SignInForm onSubmit={handleSubmit} showPassword={showPassword} />}
    </Formik>
  );
};

export default SignIn;
